column.script:


local x, y;
local b = 2;
local quad = myPlot.quad_series;

graphRoundValue = 0.1;
labelRoundValue = 0.1;

local function redrawFunction()
quad:Clear()
  for x = -100, 100, 0.1 do
    y = 1*math.pow(b,x);
    quad:Add(x,y);
  end
  quad:Update();
end

local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;

xAxis:Add(-100,0)
xAxis:Add(100,0)

yAxis:Add(0,-100)
yAxis:Add(0,100)

redrawFunction();


local i = b;
local function updateEquation()
  newEquation.FormattedText = "Your Equation: y = " ..i.."<sup>x</sup>"
end

local function updateAValue(value)
  i = round(value,labelRoundValue);
end

local function updateBValue(value)
  j = round(value,labelRoundValue)
end

local function updateCValue(value)
  k = round(value,labelRoundValue)
end

updateEquation()

 function round(value,multiple)
  result = 0;

  if value % multiple ~= 0 then
    result = multiple;
    division = math.floor((value/multiple)+1);
    result = division*multiple;
    return result;
  else 
    return value;
  end
end

slider.onValueChanged:

b = round(value,graphRoundValue);
bLabel.Text = "b = "..round(value,labelRoundValue)
redrawFunction();
updateAValue(value);
updateEquation();

column.script:


local x, y;
local a = 1;
local b = 2;
local c = 0;
local d = 1; 
local e = 1;
local expo = myPlot.exponential;

local function redrawFunction()
  expo:Clear();
  for x = -100, 100, 0.1 do
    y = a*math.pow(b,e*(x-d)) + c;
    expo:Add(x,y);
  end
  expo:Update();
end

local function computeFunction(x)
  y = a*math.pow(b,e*(x-d)) + c;
  return y;
end

local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;

xAxis:Add(-100,0)
xAxis:Add(100,0)

yAxis:Add(0,-100)
yAxis:Add(0,100)

redrawFunction();


local a2 = a;
local b2 = b;
local c2 = c;
local d2 = math.abs(round(d,1));
local e2 = e;
local yInter = 0;
local function updateEquation()
  if d2 < 0 then
    newEquation.FormattedText = "Your Equation: y = "..a2.."("..b2.."<sup>["..e2.."(x + "..math.abs(d2)..")]</sup>) + ".. c2
  elseif d2 == 0 then
    newEquation.FormattedText = "Your Equation: y = "..a2.."("..b2.."<sup>"..e2.."(x)</sup>) + ".. c2
  else
    newEquation.FormattedText = "Your Equation: y = "..a2.."("..b2.."<sup>["..e2.."(x - "..math.abs(d2)..")]</sup>) + ".. c2
  end
  parameters.FormattedText = "Horizontal Asymptote: y = "..c2;
  yInter = round(computeFunction(0),labelRoundValue);
  yIntercept.FormattedText = "Y-Intercept: (0,"..yInter..")";
end

local function updateAValue(value)
  a2 = round(value,labelRoundValue);
end

local function updateBValue(value)
  b2 = round(value,labelRoundValue);
end

local function updateCValue(value)
  c2 = round(value,labelRoundValue);
end

local function updateDValue(value)
  d2 = round(value,1);
end

local function updateEValue(value)
  e2 = round(value,labelRoundValue);
end

updateEquation()

function round(value,multiple)
  result = 0;

  if value % multiple ~= 0 then
    result = multiple;
    division = math.floor((value/multiple)+1);
    result = division*multiple;
    return result;
  else 
    return value;
  end
end

slider.onValueChanged:

a = round(value, graphRoundValue)
aLabel.Text = "a = "..round(value,labelRoundValue)
redrawFunction();
updateAValue(value)
updateEquation();


b = round(value, graphRoundValue);
bLabel.Text = "b = "..round(value,labelRoundValue)
redrawFunction();
updateBValue(value)
updateEquation();


c = round(value, graphRoundValue)
cLabel.Text = "c = "..round(value,labelRoundValue)
redrawFunction();
updateCValue(value)                  
updateEquation();


d = round(value, 1)
dLabel.Text = "d = "..round(value,1)
redrawFunction();
updateDValue(value)
updateEquation();


e = round(value, 1)
kLabel.Text = "k = "..round(value,1)
redrawFunction();
updateEValue(value)
updateEquation();

