column.script:

function round(value,multiple)
   result = 0;

   if value % multiple ~= 0 then
     result = multiple;
     division = math.floor((value/multiple)+1);
     result = division*multiple;
     return result;
   else 
     return value;
   end
 end

button.onClick:

a = table:Child(1):Child(1):Child(3):Child(2).Value;
b = table:Child(1):Child(1):Child(4):Child(2).Value;

if a == 2 and b == 2 then
  table:Child(1):Child(1):Child(6):Child(1).Text = "Correct!";
  table:Child(1):Child(1):Child(6):Child(1).Style.Visibility = "visible";
  table:Child(1):Child(1):Child(6):Child(2).Style.Visibility = "visible";
else
  table:Child(1):Child(1):Child(6):Child(1).Text = "Incorrect! Try again.";
  table:Child(1):Child(1):Child(6):Child(1).Style.Visibility = "visible";
end

