local x, y;
local x2,y2;
local graph = myPlot.graph;

local function redrawFunction()
  graph:Clear();
  for x = -100,100,0.1 do
    y = 1/(math.pow(x,2))
    graph:Add(x,y);
  end

  graph:Update();
end


local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;

xAxis:Add(-100,0)
xAxis:Add(100,0)

yAxis:Add(0,-100)
yAxis:Add(0,100)

redrawFunction();



local x, y;
local x2,y2;
local graph = myPlot.graph;

local function redrawFunction()
  graph:Clear();
  for x = -100,100,0.1 do
    y = math.sqrt(x)
    graph:Add(x,y);
  end

  graph:Update();
end


local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;

xAxis:Add(-100,0)
xAxis:Add(100,0)

yAxis:Add(0,-100)
yAxis:Add(0,100)

redrawFunction();



local x, y;
local x2,y2;
local graph = myPlot.graph;
local graph2 = myPlot.graph2;

local function redrawFunction()
  graph:Clear();
  graph2:Clear()
  for x = -100,1.6,0.1 do
    y = (math.pow(x,2) - 2*x)/(math.pow(x,2) - 4)
    graph:Add(x,y);
  end

  for x2 = 2.4,10,0.1 do
    y2 = (math.pow(x2,2) - 2*x2)/(math.pow(x2,2) - 4)
    graph2:Add(x2,y2);
  end

  graph:Update();
  graph2:Update();
end

plotTools.drawCircle(myPlot.hole, vector({2,0.5}), 0.4)

local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;

xAxis:Add(-100,0)
xAxis:Add(100,0)

yAxis:Add(0,-100)
yAxis:Add(0,100)

redrawFunction();
