stack.script:

local red = 255
local green = 0
local blue = 0
local myColor = color.rgb(255, 0, 0)
oldX = 0;
oldY = 0;

drawing.onPan:
local x = panPlot.XAxis.Min + state.Current.X * (panPlot.XAxis.Max - panPlot.XAxis.Min); local y = panPlot.YAxis.Min + state.Current.Y * (panPlot.YAxis.Max - panPlot.YAxis.Min);
if state.IsStart then panSeries:Add(oldX,oldY,color.transparent) panSeries:Add(x,y,color.transparent) panSeries:Add(x,y,myColor) else panSeries:Add(x,y,myColor) end
oldX = x oldY = y

button.onClick:
if btn2.Text == "Write Notes" then
  btn2.Text = "Hide Notes"
  section4.stack1.noteInput.Style.Visibility = "visible";
elseif btn2.Text == "Hide Notes" then
  btn2.Text = "Show Notes"
  section4.stack1.noteInput.Style.Visibility = "collapsed";
elseif btn2.Text == "Show Notes" then
  btn2.Text = "Hide Notes"
  section4.stack1.noteInput.Style.Visibility = "visible";
end

if btn3.Text == "Show Answer" then
  section4.stack1.xy1.Style.Visibility = "visible";
  section4.stack1.xy1.fun1:Clear();
  section4.stack1.sim:Stop();
  section4.stack1.sim:Start();
  btn3.Text = "Hide Answer"
elseif btn3.Text == "Hide Answer" then
  section4.stack1.sim:Stop();
  section4.stack1.xy1.Style.Visibility = "collapsed";
  btn3.Text = "Show Answer";
end

simulation.onUpdate:
local series = xy1.fun1;
local x = (time - 2)
series:Add(x, 3* math.pow(x, 3) - 2*math.pow(x, 2) - 5*x)

