slider.onValueChanged:
pieData[1] = value;

if (100 - pieData[1] - pieData[2]) >= 0 then
  pieData[3] = 100-pieData[1]-pieData[2];
else
  pieData[3] = 0;
end

if (pieData[1]+pieData[2] <= 100.5) then
  p2.Series[1]:Clear();
  p2.Series[2]:Clear();
  p2.Series[3]:Clear();
  chart.pie(p2, pieData);
  p2.Series[1]:Update();
  p2.Series[2]:Update();
  p2.Series[3]:Update();
  local denominator = 100;
  local val = round(value, 1);
  lab1.Text = "Your Percentage: " .. tostring(round(value, 1)) .. "%";
  lab2.Text = "Your Fraction: " .. tostring(val) .. "/" .. tostring(100);
else
  lab1.Text = "Your Percentage: " .. tostring(round(value, 1)) .. "%" .. " Warning, your pie pieces exceed 100%!";
end

pieData[2] = value;

if (100 - pieData[1] - pieData[2]) >= 0 then
  pieData[3] = 100-pieData[1]-pieData[2];
else
  pieData[3] = 0;
end

if (pieData[1]+pieData[2] <= 100.5) then
  local denominator = 100;
  local val = round(value, 1);
  lab3.Text = "Friend's Percentage: " .. tostring(round(value, 1)) .. "%";
  lab4.Text = "Friend's Fraction: " .. tostring(val) .. "/" .. tostring(100);
  p2.Series[1]:Clear();
  p2.Series[2]:Clear();
  p2.Series[3]:Clear();
  chart.pie(p2, pieData);
  p2.Series[1]:Update();
  p2.Series[2]:Update();
  p2.Series[3]:Update();
else
  lab3.Text = "Friend's Percentage: " .. tostring(round(value, 1)) .. "%" .. " Warning, your pie pieces exceed 100%!";
end

local val = round(value, 1);
for i=applesTaken.count, 1, -1 do
  if val >= i then
    applesTaken[i] = 1;
  else
    applesTaken[i] = 0;
  end
  if (i <= 6) then
    if(applesTaken[i] == 1) then
      if (string.find(t1:Child(1):Child(2):Child(i).ImageSource,"resources/apple.png")) then
        t1:Child(1):Child(2):Child(i).ImageSource = "resources/apple2.png";
        t1:Child(1):Child(3+2):Child(i).Style.Visibility = "visible";
        appleCount = appleCount + 1;
      end
    else
      if (string.find(t1:Child(1):Child(2):Child(i).ImageSource,"resources/apple2.png")) then
        t1:Child(1):Child(2):Child(i).ImageSource = "resources/apple.png";
        t1:Child(1):Child(3+2):Child(i).Style.Visibility = "hidden";
        appleCount = appleCount - 1;
      end
    end
  else
    if(applesTaken[i] == 1) then
      if (string.find(t1:Child(1):Child(3):Child(i-6).ImageSource,"resources/apple.png")) then
        t1:Child(1):Child(3):Child(i-6).ImageSource = "resources/apple2.png";
        t1:Child(1):Child(2+2):Child(i-6).Style.Visibility = "visible";
        appleCount = appleCount + 1;
      end
    else
      if (string.find(t1:Child(1):Child(3):Child(i-6).ImageSource,"resources/apple2.png")) then
        t1:Child(1):Child(3):Child(i-6).ImageSource = "resources/apple.png";
        t1:Child(1):Child(2+2):Child(i-6).Style.Visibility = "hidden";
        appleCount = appleCount - 1;
      end
    end
  end
end

lab1.Text = "Your Percentage: " .. tostring(round(appleCount*100/12, 0.1)) .. "%";
lab2.Text = "Your Fraction: " .. tostring(appleCount) .. "/12";

