button.onClick:
ans1.Style.Visibility = "visible";
local i1 = tonumber(section7.content:Child(3):Child(1).Text);
local i2 = tonumber(section7.content:Child(3):Child(2).Text);
local i3 = tonumber(section7.content:Child(3):Child(3).Text);
local i4 = tonumber(section7.content:Child(3):Child(4).Text);
local i5 = tonumber(section7.content:Child(3):Child(5).Text);
local i6 = tonumber(section7.content:Child(3):Child(6).Text);
if i1==null then
  i1=0;
end
if i2==null then
  i2=0;
end
if i3==null then
  i3=0;
end
if i4==null then
  i4=0;
end
if i5==null then
  i5=0;
end
if i6==null then
  i6=0;
end
local difference = i1+i2+i3+i4+i5+i6 - 846;

if (difference == 0) then
  if (section7.content:Child(3):Child(1).Text == "125") and
  (section7.content:Child(3):Child(2).Text == "100") and
  (section7.content:Child(3):Child(3).Text == "120") and
  (section7.content:Child(3):Child(4).Text == "60") and
  (section7.content:Child(3):Child(5).Text == "210") and
  (section7.content:Child(3):Child(6).Text == "231") then
    ans1.result.Text = "Great job! Your numbers are spot on. You've got a promotion coming your way!";
  else
    ans1.result.Text = "There seems to be something wrong. Please make sure you fill out the graph according to the table.";
  end
elseif (math.abs(difference) < 5) then
  ans1.result.Text = "Good job; however, your numbers could use some improvement. Check your accuracy!";
else
  ans1.result.Text = "Unacceptable, please go over your numbers again and make sure you are more accurate!";
end
