button.onClick:
if zeroButton.Text == "Undo" then
zeroButton.Text ="Zero Angle";
mySection.resultStackVisible.Style.Visibility = "visible";
mySection.resultStack2.Style.Visibility = "collapsed";
x = 0
else
zeroButton.Text ="Undo";
mySection.resultStackVisible.Style.Visibility = "collapsed";
mySection.resultStack2.Style.Visibility = "visible";
x = 1
end
return x;

stack.script:
local selection = -1;
local deselectedColor = color.silver;
local selectedColor = color.rgb(50, 133, 184);

local function chooseButton(i)
if selection > 0 then
stack.table:Child(selection):Child(2):Child(1).Style.BackgroundColor = deselectedColor;
end
stack.table:Child(i):Child(2):Child(1).Style.BackgroundColor = selectedColor;
selection = i;
end

button.onClick:
if string.lower(final.Text) == string.lower(answer.Text) then
solution.Style.Visibility = "visible";
hint.Style.Visibility = "collapsed";
else
solution.Style.Visibility = "collapsed";
hint.Style.Visibility = "visible";
end

