stack.script:


 local secondPlot = sinePlot.mySine;



 local a = 1;
 local k = 10;
 local c = 0;
 local d = 2;


  for x = -2, 36, 0.1 do

   y = math.cos(10*x*(math.pi/180)) + 2
   secondPlot:Add(x,y)
   end
   secondPlot:Update()





 local function drawLine()

   secondPlot:Clear()

    equation.FormattedText = string.format("<b>Your Answer:</b> \t y = <b> %.1f </b> cos [<b> %.1f </b> (x - <b> %.1f </b>)] + <b> %.1f </b>", a, 360/k, c,d)


   for x = -2, 36, 0.1 do
   y = a*math.cos((360/k)*math.pi/180*(x-c)) + d 
   secondPlot:Add(x,y)
   end
   secondPlot:Update()
 end



 function round(value,multiple)
 result = 0;

if value % multiple ~= 0 then
result = multiple;
division = math.floor((value/multiple)+1);
result = division*multiple;
return result;
else 
return value;
end
end

button.onClick:
if answerButton.Text == "Check Your understanding" then
answerTitle.Style.Visibility = "visible";
table.Style.Visibility = "visible";
table:Child(1):Child(2):Child(1).Style.Visibility = "visible";
answerButton.Text = "Hide Answer"
else
table:Child(1):Child(2):Child(1).Style.Visibility = "collapsed";
table.Style.Visibility = "collapsed";
answerTitle.Style.Visibility = "collapsed";
answerButton.Text = "Check Your understanding";
end

