column.script:

local xAxis = myPlot.xAxis;
local yAxis = myPlot.yAxis;
xAxis:Add(-100,0)
xAxis:Add(100,0)
yAxis:Add(0,-100)
yAxis:Add(0,100)

  local cos = myPlot.cosinePlot;
   a = 5;
   c = math.pi;
   b = 5;

  local function redrawCosine()
    cos:Clear()
    for x = -10, 10, 0.01 do
      y = a*math.cos(c*x) + b
      cos:Add(x,y)
    end
    cos:Update()
  end

  redrawCosine();


i = 5;
jValue = 1;
j = ((jValue*math.pi*180)/math.pi);
k = 5;
local function updateEquation()
  newEquation.FormattedText = string.format("<b>Your Answer: y = %d</b>",i).."<b> cos ("..j.."x) + "..k.."</b>"
end

function updateAValue(value)
  i = round(value,1);
end

function updateKValue(value)
  j = round(math.floor(((value*math.pi*180)/math.pi)),90);
end

function updateBValue(value)
  k = round(value,1)
end

updateEquation()

 function round(value,multiple)
  result = 0;

  if value % multiple ~= 0 then
    result = multiple;
    division = math.floor((value/multiple)+1);
    result = division*multiple;
    return result;
  else 
    return value;
  end
end

slider.onValueChanged:

a = round(value,1);
aLabel.Text = "a = "..round(value,1)
updateAValue(value);
updateEquation();
redrawCosine()


b = round(value,1)
dLabel.Text = "d = "..round(value,1)
updateBValue(value);
updateEquation();
redrawCosine()


c = round(value*math.pi,(math.pi/2))
redrawCosine()
displayVal = round(((value*math.pi*180)/math.pi),90);
kLabel.FormattedText = string.format("k = %d", (displayVal));
updateKValue(value);
updateEquation();
